//人员管理配置
const personOptions = {
  namespaced:true,
  actions:{
    addPersonWang(contexe,value){
      if(value.name.indexOf('王') === 0){
        contexe.commit('ADD_PERSON',value)
      }else{
        alert('添加的人必须姓王')
      }
    }
  },
  mutations:{
    ADD_PERSON(state,value){
      state.personList.unshift(value)
    }
  },
  state:{
    personList:[
      {id:'001',name:'张三'}
    ]
  },
  getters:{
    firstPersonName(state){
      return state.personList[0].name
    }
  },
}

export default personOptions
