//引入Vue
import Vue from 'vue'
//引入App
import App from './App.vue'

//关闭vue生产提示
Vue.config.productionTip = false


//创建vm
new Vue({
  el:'#app',
  render: h => h (App),
  beforeDestroy(){
    Vue.prototype.$bus = this
  }
})
