//该文件用于创建Vuex中最核心的store
//引入Vue
import Vue from'vue'
//引入vuex
import Vuex from 'vuex'
//使用Vuex插件
Vue.use(Vuex)

//求和功能相关配置
const countOptions = {
  actions:{
    jiaOdd(context,value){
      if(context.state.sum %2){
        context.commit('JIA',value)
      }
    },
    jiaWait(context,value){
      setTimeout(()=>{
        context.commit('JIA',value)
      },500)
    }
  },
  mutations:{
    JIA(state,value){
      state.sum += value
    },
    JIAN(state,value){
      state.sum -= value
    },
  },
  state:{
    sum:0,
    school:'尚硅谷',
    subject:'Vue'
  },
  getters:{
    bigSum(state){
      return state.sum *10
    }
  },
}

//人员管理配置
const personOptions = {
  actions:{},
  mutations:{
    ADD_PERSON(state,value){
      state.personList.unshift(value)
    }
  },
  state:{
    personList:[
      {id:'001',name:'张三'}
    ]
  },
  getters:{},
}

//准备actions——用于响应组件中的动作
// const actions ={}
//准备mutations——用于操作数据（state）
// const mutations ={}
//准备state——用于存储数据
// const state ={}
//准备getters——用于将state中的数据进行加工
// const getters ={}

//创建store
const store = new Vuex.Store({
  modules:{
    countOptions,
    personOptions
  }
})

//导出store
export default store
